pma:database.pma:table:
CREATE TABLE `questions` (
  `question_id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID of each question',
  `question_testID` int(11) NOT NULL COMMENT 'ID of the test where the question/s belong to',
  `question_formattedQuestion` varchar(255) NOT NULL COMMENT 'Formatted question text for the client',
  PRIMARY KEY (`question_id`)
) ENGINE=InnoDB AUTO_INCREMENT=750 DEFAULT CHARSET=latin1 COMMENT='Entry of questions for reference table tests';
